import React, { useState, useEffect } from 'react';
import { 
  Container, Typography, Box, TextField, Button, 
  Paper, Avatar, IconButton, Dialog, DialogTitle, 
  DialogContent, DialogActions, Chip, CircularProgress
} from '@mui/material';
import { Add, Delete, Edit, Palette } from '@mui/icons-material';
import { motion } from 'framer-motion';
import axios from 'axios';
import './App.css';

const ColorOptions = [
  '#4CAF50', '#2196F3', '#9C27B0', '#FF5722', 
  '#FFEB3B', '#607D8B', '#E91E63', '#00BCD4'
];

function App() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [open, setOpen] = useState(false);
  const [form, setForm] = useState({
    name: '',
    description: '',
    color: '#4CAF50'
  });
  const [editingId, setEditingId] = useState(null);

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      setLoading(true);
      const { data } = await axios.get('http://backend:5000/api/items');
      setItems(data);
    } catch (error) {
      console.error('Error fetching items:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await axios.put(`http://backend:5000/api/items/${editingId}`, form);
      } else {
        await axios.post('http://backend:5000/api/items', form);
      }
      setOpen(false);
      setForm({ name: '', description: '', color: '#4CAF50' });
      setEditingId(null);
      fetchItems();
    } catch (error) {
      console.error('Error saving item:', error);
    }
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Box 
        component={motion.div}
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        sx={{ 
          background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
          p: 3, 
          borderRadius: 2, 
          mb: 4,
          boxShadow: 3
        }}
      >
        <Typography variant="h3" color="white" fontWeight="bold">
          Funky CRUD App ðŸŽ‰
        </Typography>
      </Box>

      <Button
        variant="contained"
        startIcon={<Add />}
        onClick={() => setOpen(true)}
        sx={{ mb: 3 }}
      >
        Add New Item
      </Button>

      {loading ? (
        <Box display="flex" justifyContent="center" mt={4}>
          <CircularProgress color="secondary" />
        </Box>
      ) : (
        <Box 
          component={motion.div}
          layout
          sx={{ 
            display: 'grid', 
            gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', 
            gap: 3 
          }}
        >
          {items.map((item) => (
            <Paper 
              key={item.id}
              component={motion.div}
              layout
              initial={{ scale: 0.9 }}
              animate={{ scale: 1 }}
              sx={{ 
                p: 3, 
                borderRadius: 2,
                borderLeft: `5px solid ${item.color}`,
                transition: 'transform 0.2s',
                '&:hover': {
                  transform: 'translateY(-5px)'
                }
              }}
            >
              <Box display="flex" justifyContent="space-between">
                <Typography variant="h6" fontWeight="bold">
                  {item.name}
                </Typography>
                <Chip 
                  label={item.color} 
                  size="small" 
                  sx={{ 
                    backgroundColor: item.color, 
                    color: 'white',
                    fontWeight: 'bold'
                  }} 
                />
              </Box>
              <Typography variant="body2" sx={{ mt: 1, mb: 2 }}>
                {item.description}
              </Typography>
              <Box display="flex" justifyContent="flex-end" gap={1}>
                <IconButton onClick={() => {
                  setForm({
                    name: item.name,
                    description: item.description,
                    color: item.color
                  });
                  setEditingId(item.id);
                  setOpen(true);
                }}>
                  <Edit color="primary" />
                </IconButton>
                <IconButton onClick={async () => {
                  if (window.confirm('Delete this funky item?')) {
                    await axios.delete(`http://localhost:5000/api/items/${item.id}`);
                    fetchItems();
                  }
                }}>
                  <Delete color="error" />
                </IconButton>
              </Box>
            </Paper>
          ))}
        </Box>
      )}

      <Dialog open={open} onClose={() => setOpen(false)}>
        <DialogTitle>
          {editingId ? 'Edit Funky Item' : 'Create Funky Item'}
        </DialogTitle>
        <DialogContent>
          <Box component="form" onSubmit={handleSubmit} sx={{ mt: 2 }}>
            <TextField
              fullWidth
              label="Name"
              value={form.name}
              onChange={(e) => setForm({...form, name: e.target.value})}
              margin="normal"
              required
            />
            <TextField
              fullWidth
              multiline
              rows={4}
              label="Description"
              value={form.description}
              onChange={(e) => setForm({...form, description: e.target.value})}
              margin="normal"
            />
            <Box sx={{ mt: 2 }}>
              <Typography variant="subtitle2" gutterBottom>
                Select Color:
              </Typography>
              <Box display="flex" gap={1} flexWrap="wrap">
                {ColorOptions.map((color) => (
                  <Avatar
                    key={color}
                    sx={{ 
                      backgroundColor: color, 
                      cursor: 'pointer',
                      border: form.color === color ? '3px solid #1976D2' : 'none',
                      width: 32, 
                      height: 32 
                    }}
                    onClick={() => setForm({...form, color})}
                  >
                    {form.color === color && <Palette sx={{ color: 'white' }} />}
                  </Avatar>
                ))}
              </Box>
            </Box>
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpen(false)}>Cancel</Button>
          <Button 
            onClick={handleSubmit} 
            variant="contained"
            disabled={!form.name}
          >
            {editingId ? 'Update' : 'Create'}
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default App;
